import React, { useState } from 'react';
import './songBar.css';
import { useAuth } from '../../hooks/authContext';

function songBar({ index, song_title, artist, url }) {
  const { user } = useAuth();
  const [isLikedSong, setIsLikedSong] = useState(false);

  async function isLiked ({ song_title }){
    // needed this bcs some songs have spaces
    const encodedTitle = encodeURIComponent(song_title);
    const apiURL = `api/is-liked?song_title=${encodedTitle}&username=${user}`;

    const response = await fetch(apiURL, {
      method: "GET",
    });

    if(response.ok){
      setIsLikedSong(true)
      return;
    }
    setIsLikedSong(false)
  }

  async function likeSong ({song_title}){
    const apiURL = "/api/like";

    const response = await fetch(apiURL, {
      method: 'POST',
      headers:{
        'Content-Type': 'application/json',
        'Authorization': user.username
      },
      body: JSON.stringify({
        song_title: song_title
      })
    });
    console.log(response);
    if(response.ok){
      setIsLikedSong(true);
    }
  }

  return (
    <div className='song-bar'>
      <div>
        <p className='index'>{index}.</p>
      </div>
      <div className='aspect-ratio-wrapper'>
        <img 
          src={url}
          alt="song-img"
          className='image'
        />
      </div>
      <div className='song-info'>
        <p className='song-title'>{song_title}</p>
        <p className='artist'>{artist}</p>
      </div>
      <div className='like-button-container'>
        <svg 
          xmlns="http://www.w3.org/2000/svg" 
          height="40px" 
          viewBox="0 -960 960 960" 
          width="40px" 
          fill="#444444"
          className={isLikedSong ? "like-button" : ""}
          onClick={() => likeSong(song_title)}
        >
            <path d="m480-120-58-52q-101-91-167-157T150-447.5Q111-500 95.5-544T80-634q0-94 63-157t157-63q52 0 99 22t81 62q34-40 81-62t99-22q94 0 157 63t63 157q0 46-15.5 90T810-447.5Q771-395 705-329T538-172l-58 52Zm0-108q96-86 158-147.5t98-107q36-45.5 50-81t14-70.5q0-60-40-100t-100-40q-47 0-87 26.5T518-680h-76q-15-41-55-67.5T300-774q-60 0-100 40t-40 100q0 35 14 70.5t50 81q36 45.5 98 107T480-228Zm0-273Z"/>
        </svg>
      </div>
    </div>
  );
}

export default songBar;
